const {
  ApolloServer,
  gql,
  UserInputError
} = require('apollo-server');

const typeDefs = gql`
  type Query {
    userWithID(id: ID!): User
  }

  type User {
    id: ID!
    name: String!
  }
`;

const resolvers = {
  Query: {
    userWithID: (parent, args, context) => {
      if (args.id < 1) {
        throw new UserInputError('Invalid argument value');
      }
      // ...fetch correct user...
    },
  },
};

const {
  ApolloServer,
  gql,
  UserInputError
} = require('apollo-server');

const typeDefs = gql`
  type Query {
    userWithID(id: ID!): User
  }

  type User {
    id: ID!
    name: String!
  }
`;

const resolvers = {
  Query: {
    userWithID: (parent, args, context) => {
      if (args.id < 1) {
        throw new UserInputError('Invalid argument value', {
          argumentName: 'id'
        });
      }
      // ...fetch correct user...
    },
  },
};

